$('#dform_mp_general_enquiry').off('_KDF_saveError').on('_KDF_saveError', function(event, kdf) {
	KDF.showError(KDF.buildValidationMessage('Oops, something went wrong in kdf_saveError'));
});	// End of KDF_saveError	

$('#dform_mp_general_enquiry').off('_KDF_save').on('_KDF_save', function(event, kdf) {
	// fires when the form has been saved
    var input_param = {
	    'caseid'    :   KDF.kdf().form.caseid, 
	    'email_address' :   KDF.getVal('email_subscriber'),
	    'subscription_type' :   'enquiry_raised',
	    'customerid':   KDF.getVal('txt_customerid_subscriber'),
	    'eventcode' :   KDF.getVal('txt_eventcode'),
	    'subject_code'  :   'All'
	};
	
	if (!not_associated) {
    	subscription(input_param);
	}
});


$('#dform_mp_general_enquiry').off('_KDF_customError').on('_KDF_customError', function( event, customaction, xhr, settings, thrownError) {
    KDF.showError(thrownError);
}); // End of KDF_customError

